export function saveLanguageToDB(language) {
    const url = "http://localhost:9000/db.php";

    fetch(url, {
        method: "POST",
        headers: {
            "Content-Type": "application/x-www-form-urlencoded",
        },
        body: `language=${encodeURIComponent(language)}`,
    })
        .then((response) => response.json()) // You may need to adjust this based on your API response format
        .then((data) => {
            console.log("YAYAYAYAYAYAYAY");
            // Handle success or additional logic here
            history.back(); // Assuming history is available in your context
        })
        .catch((error) => {
            console.error("Error saving language to DB:", error);
            // Handle error or additional error logic here
        });
}
